<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Strict//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-strict.dtd"><html xmlns="http://www.w3.org/1999/xhtml"><head>	<title>Error - Debug On</title>	<meta http-equiv="Content-Type" content="text/html; charset=<?php echo charset ?>">	<style type="text/css">		body		{			font-family: Arial;			font-size: 14px;			margin: 0;			background: #F5F5F5;		}		p		{			margin: 0;			padding: 0;		}		h1 		{			margin: 0;			margin-bottom: 5px;			padding: 0;			font-size: 2em;			color: #104E8B;		}		h2		{			margin: 0;			font-size: 1.2em;		}		.header		{			background: #C6E2FF;			padding: 15px;			border-bottom: 5px solid #777;		}		.code		{			padding: 15px;		}		.code pre, .code .pre		{			background: #FcFcFc;			font-family: monospace;			font-size: 1.2em;			padding: 15px;			margin: 10px;			border: solid 1px #DEDEDE;			/*-webkit-border-radius: 10px;			-moz-border-radius: 10px;			border-radius: 10px;*/		}		.server		{			margin: 15px;			display: none;		}		.server table		{			border-collapse: collapse;			width: 100%;			background: #FcFcFc;			font-size: 0.8em;				}		.server td 		{			padding: 5px;			border: solid 1px #DEDEDE;		}		.toggle		{			float: right;			font-size: 0.7em;		}	</style></head><body>	<div class="header">		<h1>Erro - Debug Habilitado</h1>		<p><b>Mensagem:</b> <?php echo $error->getMessage() ?></p>	</div>	<div class="code">		<h2>Detalhes:</h2>		<div class="pre"><?php echo method_exists($error, 'getDetails') ? $error->getDetails() : '' ?>		</div>		<h2>Arquivo:</h2>		<pre><?php echo $error->getFile() ?>(<?php echo $error->getLine() ?>)</pre>		<h2>Trace:</h2>		<pre><?php echo $error->getTraceAsString() ?></pre>		<h2>Server: <a href="javascript:;" onclick="toggle('server', this)" class="toggle">[exibir]</a></h2>		<div class="server" id="server">			<table>				<tbody>					<?php foreach($_SERVER as $k => $v): ?>					<tr>						<td><?php echo $k ?></td>						<td><?php echo $v ?></td>					</tr>					<?php endforeach ?>				</tbody>			</table>		</div>		<h2>Constantes: <a href="javascript:;" onclick="toggle('constants', this)" class="toggle">[exibir]</a></h2>		<div class="server" id="constants">			<table>				<tbody>					<?php $constants = get_defined_constants(true) ?>					<?php foreach($constants['user'] as $k => $v): ?>					<tr>						<td><?php echo $k ?></td>						<td><?php echo htmlentities($v) ?></td>					</tr>					<?php endforeach ?>				</tbody>			</table>		</div>		<h2>Debug: <a href="javascript:;" onclick="toggle('debug', this)" class="toggle">[exibir]</a></h2>		<div class="server" id="debug">			<?php Debug::show() ?>		</div>	</div>	<script type="text/javascript">		var toggle = function(id, t) {			var e = document.getElementById(id);			if(e.style.display == 'block') {				e.style.display = 'none';				t.innerHTML = '[exibir]';			} else {				e.style.display =  'block';				t.innerHTML = '[ocultar]';			}		};	</script></body></html>