			throw new PropertyNotFoundException($this->classname .'->'. $property);				$reflection = new ReflectionProperty($this->classname, $property);		$comment = $reflection->getDocComment();		return $this->extractAnnotation($comment);	}		/**	 * Pega a anotação todas as propriedades da classe	 * @return	array	lista com instâncias de sdtClass	 */	public function getProperties()	{		$reflection = new ReflectionClass($this->classname);		$properties = array();		foreach($reflection->getProperties(ReflectionProperty::IS_PUBLIC) as $property)		{			$reflectionProperty = new ReflectionProperty($this->classname, $property->getName());			$comment = $reflectionProperty->getDocComment();			$properties[$property->getName()] = $this->extractAnnotation($comment);		}		return $properties;	}		/**	 * Pega a anotação do método especificado	 * @param	string	$method			nome do método a ser analisado	 * @throws	MethodNotFoundException	dispara se o método não existir na classe	 * @return	object					retorna uma instância de stdClass	 */	public function getMethod($method)	{		if(!method_exists($this->classname, $method)) 			throw new MethodNotFoundException($this->classname .'->'. $method .'()');				$reflection = new ReflectionMethod($this->classname, $method);		$comment = $reflection->getDocComment();		return $this->extractAnnotation($comment);	}		/**	 * Extrai a anotação a partir de DocComment	 * @param	string	$comment	comentário da documentação	 * @return	object				retorna uma instância de stdClass	 */	private function extractAnnotation($comment)	{		preg_match_all('/@([a-zA-Z]+)\((.*)\)/', $comment, $matches);		$annotation = new stdClass;		foreach($matches[1] as $k => $v)		{			if(preg_match('/^"([a-zA-Z0-9_]+)"$/', $matches[2][$k]))			{				$annotation->{$v} = trim($matches[2][$k],'"');			}			else if(preg_match('/^"([a-zA-Z0-9,"]+)"$/', $matches[2][$k], $sub_matches))			{				$annotation->{$v} = explode('","', $sub_matches[1]);			}			else			{				$annotation->{$v} = new stdClass;				if($matches[2][$k])				{					$attrs = explode('",', $matches[2][$k]);					foreach($attrs as $a)					{						$prop = explode('="', $a);						$annotation->{$v}->{trim($prop[0])} = str_replace('"', '', $prop[1]);					}				}			}		}		return $annotation;	}}